import React, { useState } from "react";
import RegistrationWithInput from "../reusable/registrationWithInputField";
import "./index.css";
import {connect} from "react-redux";
import { bindActionCreators } from "redux";
import {userLogin,registration} from "../../redux/type";
let user = require("../../registration/registration");

const mapStateToProps=(state) =>{
  debugger;
  return {
    userLogin : state.userLogin,
    registration:state.registration,
  }
}

const mapDispatchtoProps = (dispatch) =>{
  debugger;
  return bindActionCreators(
    {
      userLogin: userLogin,
      registration:registration
    },
    dispatch,
  );
  // return{
  //   userLogin:(e) =>{
  //     dispatch(userLogin(e))
  //   },
  //   registration:(e) =>{
  //     dispatch(registration(e))
  //   }
  // }
}

const Login = (props) => {
  debugger;
  const [loginUserNameAndPassword, setLoginUserNameAndPassword] = useState({});
  const checkValidation = UserDetail => {
    debugger;
    let userName = UserDetail.userName;
    if(!user.subscribedUser[userName]){
        alert("user is not present in db");
    }
    if (
      user.subscribedUser[userName] &&
      user.subscribedUser[userName].password != UserDetail.password
    ) {
      alert("please enter correct password");
    }
  };

  const goToWelcomePage = () => {
    if (checkValidation(loginUserNameAndPassword)) {
    }
  };

  const onInputChange = e => {
      debugger
    let tagName = e.target.name;
    let value = e.target.value;
    let copyOfLoginUserNameAndPassword = { ...loginUserNameAndPassword };
    copyOfLoginUserNameAndPassword[tagName] = value;
    setLoginUserNameAndPassword(copyOfLoginUserNameAndPassword);
    console.log(loginUserNameAndPassword);
    props.userLogin(loginUserNameAndPassword)
  };

  return (
    <>
      <label className="login-header">User Login</label>
      <RegistrationWithInput
        goToWelcomePage={goToWelcomePage}
        onInputChange={onInputChange}
        inputProps={user?.loginForm}
      />
      < button onClick={goToWelcomePage}>Login</button>
    </>
  );
};

export default connect(mapStateToProps,mapDispatchtoProps)(Login);
