import React, { useState } from "react";
const RegistrationWithInput = ({
  inputProps,
  userInput,
  onInputChange,
  onSubmit,
  subscribedUser,
  ...props
}) => {
  const [errorMsg, setErrorMsg] = useState([]);
  const getInputTextTypeDom = value => {
    return (
      <div>
        <lable>{inputProps?.[value]?.lable}</lable>
        <input
          required
          type={inputProps?.[value]?.inputType}
          onChange={onInputChange}
          name={inputProps?.[value]?.tagName}
          value={userInput[value]}
        />
      </div>
    );
  };
  const getRadioButtonTypeDom = value => {
    return (
      <div>
        <label>{value} :</label>
        {inputProps[value].value.map(lable => {
          return (
            <>
              <input
                type="radio"
                value={lable}
                name={value}
                checked={lable == userInput[value]}
                onChange={onInputChange}
              />
              <lable>{lable}</lable>
            </>
          );
        })}
        {/* <lable>{inputProps?.[value]?.lable}</lable>
        <input
          type={inputProps?.[value]?.inputType}
          onChange={onInputChange}
          name={inputProps?.[value]?.tagName}
        /> */}
      </div>
    );
  };

  const validateRegistrationDetail = () => {
    debugger;
    //   if(registrationDetail)
    let errorArray = [];
    let registrationForm = inputProps;
    let registrationDetail = userInput;
    debugger;
    Object.keys(registrationForm).map(value => {
      if(subscribedUser[registrationDetail.registrationMsg]){
        errorArray.push("user is already present")
      }else if (!registrationDetail[value]) {
        let error = registrationForm[value].validations[0].msg;
        errorArray.push(error.replace("__FIELD_TITLE__", value));
      } 
    });
    setErrorMsg(errorArray);
  };

  return (
    <div>
      <div>
        {Object.keys(inputProps).map(value => {
          return (
            <div>
              {inputProps[value].type == "editableRadioButton"
                ? getRadioButtonTypeDom(value)
                : getInputTextTypeDom(value)}
              {/* <lable>{inputProps?.[value]?.lable}</lable>
              <input
                type={inputProps?.[value]?.inputType}
                onChange={onInputChange}
                name={inputProps?.[value]?.tagName}
              /> */}
            </div>
          );
        })}
      </div>
      {errorMsg.length > 0 && (
        <div>
          {errorMsg.map(value => {
            return <span>{value}</span>;
          })}
        </div>
      )}
      <button onClick={validateRegistrationDetail}>Sign Up</button>
    </div>
  );
};

export default RegistrationWithInput;
